import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:iconsax/iconsax.dart';import '../../../utils/constants/sizes.dart';import '../../utils/constants/colors.dart';import '../../utils/device/device_utility.dart';import '../../utils/helpers/helper_functions.dart';class TAppBar extends StatelessWidget implements PreferredSizeWidget {  const TAppBar(      {super.key,        this.title,        this.showBackArrow = true,        this.leadingIcon,        this.actions,        this.leadingOnPressed,        this.padding = const EdgeInsets.symmetric(horizontal: TSizes.md),        this.bgColor      });  final Widget? title;  final bool showBackArrow;  final IconData? leadingIcon;  final List<Widget>? actions;  final VoidCallback? leadingOnPressed;  final EdgeInsetsGeometry padding;  final Color? bgColor;  @override  Widget build(BuildContext context) {    final dark = THelperFunctions.isDarkMode(context);    return Padding(padding: padding,        child: AppBar(          // forceMaterialTransparency: true,          automaticallyImplyLeading: false,          leading: showBackArrow?            IconButton(onPressed: () => Get.back(), icon: const Icon(Iconsax.arrow_left), color: dark? Colors.white : TColors.dark):            leadingIcon != null ? IconButton(onPressed: leadingOnPressed, icon: Icon(leadingIcon)) : null,          title: title,          actions: actions,          backgroundColor: bgColor,        )    );  }  @override  // TODO: implement preferredSize  Size get preferredSize => Size.fromHeight(TDeviceUtils.getAppBarHeight());}